    e ||= window.event;
    e.preventDefault();
    // calculate new cursor position, set element's new position:
    hintd.style.left = (hintd.offsetLeft - posx + e.clientX) + "px";
    hintd.style.top = (hintd.offsetTop - posy + e.clientY) + "px";
    posx = e.clientX, posy = e.clientY;
